mapper.select:
SELECT 	COUNT(*)
FROM	member
WHERE	member_id = #{member_id}
AND		member_pwd = #{member_pwd}

mapper.update:
UPDATE	member
SET		lastDate = SYSDATE
WHERE 	member_id = #{member_id}

mapper.select:
SELECT 	
	
		member_id
	
	
		member_pwd
	
FROM	member
WHERE	
	
		member_name = #{member_name}
		AND member_email = #{member_email}
	
	
		member_id = #{member_id}
