    )

select * from ( select hall_id,hall_name,place_num, province,city,postNum,address, rownum rNum 
 from (select  h.hall_id hall_id,h.hall_name hall_name,p.place_num place_num, p.province province,p.city city,p.postNum postNum,p.address address from hallInfo h join place p on h.place_num=p.place_num order by hall_id desc )
) WHERE rNum >= #{start} AND rNum <= #{end}

		select h.hall_id hall_id,h.hall_name hall_name,p.place_num place_num, p.province province,p.city city,p.postNum postNum,p.address address 
		 from hallInfo h join place p on h.place_num=p.place_num 
  	
			AND hall_name LIKE #{keyword}
		
		
			AND hall_id LIKE #{keyword}
		
		
			AND province LIKE #{keyword}
		
		
			AND city LIKE #{keyword}
		
		
			AND address LIKE #{keyword}

		select count(*) 
		 from hallInfo h join place p on h.place_num=p.place_num 
  	
			AND hall_name LIKE #{keyword}
		
		
			AND hall_id LIKE #{keyword}
		
		
			AND province LIKE #{keyword}
		
		
			AND city LIKE #{keyword}
		
		
			AND address LIKE #{keyword}

SELECT * FROM( SELECT per.disc_code, per.disc_title, per.disc_price, per.disc_image, per.disc_con, per.cateNum, per.first_grade, per.disc_step, per.disc_count, cat.category, cat.mDev, cat.sDev, rownum rNum FROM perDisc per JOIN category cat ON per.cateNum = cat.cateNum
cat.category LIKE '${category}' AND cat.mDev LIKE '${mDev}' AND cat.sDev LIKE '${sDev}' AND per.disc_title LIKE '%${keyword}%'
)WHERE rNum >= #{start} AND rNum <=#{end}

SELECT * FROM( SELECT tic.ticket_num, tic.member_id, tic.per_id, tic.ticet_date,tic.round, tic.seat_type, tic.seat_num, tic.sale_div, tic.del_num, tic.ticket_step, s.sale_rate, del.del_name, del.del_addr, del.del_hp, cat.category, cat.mDev, cat.sDev, per.perf_title, rownum rNum FROM Ticketing tic JOIN delevaryInfo del ON tic.del_num = del.del_num JOIN sale s ON tic.sale_div = s.sale_div JOIN performanceInfo per ON tic.per_id = per.per_id JOIN category cat ON per.cateNum = cat.cateNum WHERE 1=1 AND tic.ticket_step LIKE '${ticket_step}' AND cat.mDev LIKE '${mDev}' AND cat.sDev LIKE '${sDev}'
AND tic.ticket_num LIKE '${keyword}' AND tic.member_id LIKE '%${keyword}%' AND per.perf_title LIKE '%${keyword}%' AND del.del_name LIKE '%${keyword}%' AND del.del_addr LIKE '%${keyword}%' AND del.del_hp LIKE '%${keyword}%'
AND( tic.ticket_num LIKE '${keyword}' OR tic.member_id LIKE '%${keyword}%' OR per.perf_title LIKE '%${keyword}%' OR del.del_name LIKE '%${keyword}%' OR del.del_addr LIKE '%${keyword}%' OR del.del_hp LIKE '%${keyword}%')
)WHERE rNum >= #{start} AND rNum <=#{end}

SELECT * FROM( SELECT dis.disc_num,dis.del_num, dis.disc_buyDate,dis.disc_code,dis.member_id, dis.disc_buyStep, dis.sale_div, s.sale_rate, del.del_name, del.del_addr,del.del_hp, cat.category, cat.mDev,cat.sDev, per.disc_title, rownum rNum FROM DiscBuy dis JOIN delevaryInfo del ON dis.del_num = del.del_num JOIN sale s ON dis.sale_div = s.sale_div JOIN perDisc per ON dis.disc_code = per.disc_code JOIN category cat ON per.cateNum = cat.cateNum WHERE 1=1 AND dis.disc_buyStep LIKE '${disc_buyStep}' AND cat.mDev LIKE '${mDev}' AND cat.sDev LIKE '${sDev}'
AND del.del_num LIKE '${keyword}' AND dis.member_id LIKE '%${keyword}%' AND per.disc_title LIKE '%${keyword}%' AND del.del_name LIKE '%${keyword}%' AND del.del_addr LIKE '%${keyword}%' AND del.del_hp LIKE '%${keyword}%'
AND( del.del_num LIKE '${keyword}' OR dis.member_id LIKE '%${keyword}%' OR per.disc_title LIKE '%${keyword}%' OR del.del_name LIKE '%${keyword}%' OR del.del_addr LIKE '%${keyword}%' OR del.del_hp LIKE '%${keyword}%' )
)WHERE rNum >= #{start} AND rNum <=#{end}

select count(*) from performanceInfo per, category ca where per.CATENUM=ca.CATENUM and MDEV=#{Hcnt}
AND per_id LIKE '${keyword}'
AND perf_title LIKE '%${keyword}%'
AND per_step LIKE '${keyword}'
AND MDEV LIKE #{Hcnt}

select count(*) from perDisc perdi, category ca where perdi.CATENUM=ca.CATENUM and category=#{Hcnt}
AND disc_code LIKE '${keyword}'
AND disc_title LIKE '%${keyword}%'
AND disc_step LIKE '${keyword}'
AND category LIKE #{Hcnt}

		  
